import br.com.phellaudiozada.Audios.Herancas.Som;import br.com.phellaudiozada.Audios.Sons.Musicas;import br.com.phellaudiozada.Audios.Sons.Podcasts;import java.io.PrintStream;import java.util.Random;import java.util.Scanner;public class TocarAudio extends Som {    public static void main(String[] args) {    PrintStream show = System.out;    Scanner scan = new Scanner (System.in);    Random aleatorio = new Random();    show.println("Qual seu nome?");    String nome = scan.nextLine();    nome = nome.substring(0,1).toUpperCase().concat(nome.substring(1));    show.println("Olá " + nome + ", seja bem-vindo!");    show.println("\nO que você quer ouvir agora? ");    show.println("\n****************************************************");    show.println("\n 1 - Música");    show.println(" 2 - Podcast");    show.println("\n****************************************************");    int escolhaDoQueOuvir = scan.nextInt();    if (escolhaDoQueOuvir == 1) {        Musicas oruam = new Musicas();        oruam.setNome("Terra Prometida");        oruam.setCantor("Oruam");        oruam.setBeatmaker("Ajaxx");        oruam.setDuracao(3);        oruam.setOuvintes(aleatorio.nextInt(5000000));        oruam.notinhas(10);        oruam.notinhas(9);        oruam.notinhas(2);        oruam.mostrarMusicas();    } else if (escolhaDoQueOuvir == 2) {        Podcasts podpah = new Podcasts();        podpah.setApresentador("Alguem1 & Alguem2");        podpah.setConvidado("Neymar");        podpah.setDuracao(2);        podpah.setOuvintes(aleatorio.nextInt(120000));        podpah.notinhas(10);        podpah.notinhas(2);        podpah.notinhas(7);        podpah.mostrarPodcast();    }    }}