package br.com.phellaudiozada.Audios.Herancas;public class Som {    private String nome;    private int duracao;    private int ouvintes;    private double somaDasNotas;    private int notasTotais;    public double getSomaDasNotas() {        return somaDasNotas;    }    public void setSomaDasNotas(double somaDasNotas) {        this.somaDasNotas = somaDasNotas;    }    public int getNotasTotais() {        return notasTotais;    }    public void setNotasTotais(int notasTotais) {        this.notasTotais = notasTotais;    }    public String getNome() {        return nome;    }    public void setNome(String nome) {        this.nome = nome;    }    public double getDuracao() {        return duracao;    }    public void setDuracao(int duracao) {        this.duracao = duracao;    }    public int getOuvintes() {        return ouvintes;    }    public void setOuvintes(int ouvintes) {        this.ouvintes = ouvintes;    }    public void notinhas(double nota) {        somaDasNotas += nota;        notasTotais++;    }    public void mostrarMusicas() {        System.out.println("Nota: " + somaDasNotas);    }}