package br.com.phellaudiozada.Audios.Sons;import br.com.phellaudiozada.Audios.Herancas.Som;public class Musicas extends Som {    private String cantor;    private String beatmaker;    public String getCantor() {        return cantor;    }    public void setCantor(String cantor) {        this.cantor = cantor;    }    public String getBeatmaker() {        return beatmaker;    }    public void setBeatmaker(String beatmaker) {        this.beatmaker = beatmaker;    }    public void mostrarMusicas() {        System.out.println("\nTocando: " + getNome());        System.out.println("Cantor: " + getCantor());        System.out.println("BeatMaker: " + getBeatmaker());        System.out.println("Duração: " + getDuracao()  + " minutos");        System.out.println("Ouvintes: " + getOuvintes());        System.out.println("Nota: " + getSomaDasNotas());    }}