package br.com.phellaudiozada.Audios.Sons;import br.com.phellaudiozada.Audios.Herancas.Som;public class Podcasts extends Som {    private String apresentador;    private String convidado;    public String getApresentador() {        return apresentador;    }    public void setApresentador(String apresentador) {        this.apresentador = apresentador;    }    public String getConvidado() {        return convidado;    }    public void setConvidado(String convidado) {        this.convidado = convidado;    }    public void mostrarPodcast() {        System.out.println("Apresentador " + getApresentador());        System.out.println("Convidado: " + getConvidado());        System.out.println("Duração " + getDuracao() + " horas");        System.out.println("Ouvintes: " + getOuvintes());        System.out.println("Nota: " + getSomaDasNotas());    }}